mapper.insert:
BEGIN insert into cc_skill_agent (cts, company_id, skill_id, agent_id, rank_value, status) values
(#{item.cts,jdbcType=BIGINT}, #{item.companyId,jdbcType=BIGINT},#{item.skillId,jdbcType=BIGINT},#{item.agentId} , #{item.rankValue,jdbcType=INTEGER}, #{item.status,jdbcType=INTEGER})
COMMIT; END;

mapper.update:
update cc_skill_agent
set uts        = #{uts,jdbcType=BIGINT},
    rank_value = #{rankValue,jdbcType=INTEGER}
where skill_id = #{skillId,jdbcType=BIGINT}
  and agent_id = #{agentId,jdbcType=BIGINT}
